module.exports.manager = function (argsCheck, args, message, client, name) {
  var badArgs = [],
      requiredNum = 0;
      argsCheck.forEach(function (req) {
        if (req.required) requiredNum++;
      });
      argsCheck.forEach(function (req, i) {
          if (req.type == "number" && !isNaN(args[i])) {

          } else if (req.type == "string" && typeof args[i] == "string") {

          } else if (args[i]) {
            badArgs += req.name
          }
      });
      if (badArgs.length > 0 || requiredNum > args.length) {
        message.channel.send(`There was an error, ${badArgs.length > 0 ? "one or more of your arguments is the wrong type": `${argsCheck.length} ${argsCheck.length == 1 ? "argument is" : "arguments are"} required & ${args.length} ${argsCheck.length == 1 ? "was" : "were"}`} given`);
        client.commands.get("lhelp").execute(message, [name], client);
        console.log(name);
        return false;
    }
    return true;
  }